import{j as i,L as e,a}from"./index-d98f6bca.js";import{P as l}from"./index-6e13718b.js";const m=()=>i(e,{formLayout:!0,children:[a(l,{title:"GİZLİLİK POLİTİKASI"}),i("section",{children:[a("p",{className:"mt-5",children:"İşbu Kişisel Verilerin Korunması ve Gizlilik Politikası (“Politika”), kişisel verilerin ... APP tarafından toplanması, kullanılması, paylaşması ve saklanması süreçleri ve prensipleri hakkında sizleri bilgilendirmek amacıyla hazırlanmıştır. Amacımız depremden etkilenenlere gerekli yardım ve desteği sağlamaktır ve yardım sağlamak amacıyla bilgileri devlet kurumları ve sivil toplum kuruluşları (STK'lar) ile paylaşabiliriz."}),a("p",{className:"mt-5",children:"Kişisel bilgilerinizin gizliliğini korumayı ve 6698 sayılı Kişisel Verilerin Korunması Kanunu (“KVKK” veya “Kanun”) ve ilgili mevzuat kapsamında ilgili tüm yasalara uymayı taahhüt ediyoruz. Bu politika, kişisel bilgilerinizin toplanması, kullanılması, saklanması ve paylaşılmasına ilişkin haklarınızı ve yükümlülüklerimizi ortaya koymaktadır. İşbu metin ile, veri sorumlusu sıfatıyla KVKK’nın 10. maddesinde belirtilen aydınlatma yükümlülüğünü yerine getirmekte ve böylelikle, 'afetharita.com' aracılığıyla erişilen (“İnternet Sitesi”) üzerinden gerçekleştirdiğiniz faaliyetler özelinde işlemiş olduğumuz kişisel verilerinizin işlenme süreçleri hakkında siz ziyaretçilerimizi detaylıca bilgilendirmeyi hedeflemekteyiz."}),i("div",{className:"mt-5",children:[a("h3",{className:"text-1xl font-bold",children:"Hangi Verileri Topluyoruz?"}),a("p",{className:"mt-5",children:"Uygulamamıza depremzede olarak kaydolan kullanıcılardan ad, soyad, telefon numarası ve konum verilerini topluyoruz. Bu veriler yardım ve kaynakların ihtiyaç sahiplerine dağıtılmasına yardımcı olmak amacıyla toplanmaktadır. Bu veriler, depremden etkilenen kişilerin yerini ve sayısını belirlememize ve yardımları buna göre dağıtmamıza yardımcı olur."})]}),i("div",{className:"mt-5",children:[a("h3",{className:"text-1xl font-bold",children:"Kişisel Veri Toplamanın Yöntemi ve Hukuki Sebepler"}),a("p",{className:"mt-5",children:"Kişisel verileriniz yardım ve kaynakların ihtiyaç sahiplerine dağıtılmasına yardımcı olmak için kullanılacaktır ve yalnızca aşağıda belirtilen sebepler doğrultusunda toplanacaktır; • KVKK Madde 5/2- b, uyarınca fiili imkânsızlık nedeniyle rızasını açıklayamayacak durumda bulunan veya rızasına hukuki geçerlilik tanınmayan kişinin kendisinin ya da bir başkasının hayatı veya beden bütünlüğünün korunması için zorunlu olması, • KVKK Madde 5/2- e. uyarınca bir hakkın tesisi, kullanılması veya korunması için veri işlemenin zorunlu olmasına dayanmaktadır. • KVKK Madde 5/2(d) uyarınca ilgili kişinin kendisi tarafından alenileştirilmiş olması, • KVKK Madde 28/1-(ç) uyarınca, kişisel verilerin millî savunmayı, millî güvenliği, kamu güvenliğini, kamu düzenini veya ekonomik güvenliği sağlamaya yönelik olarak kanunla görev ve yetki verilmiş kamu kurum ve kuruluşları tarafından yürütülen önleyici, koruyucu ve istihbarı faaliyetler kapsamında işlenmesi, • KVKK Madde 28/2-(b) uyarınca, ilgili kişinin kendisi tarafından alenileştirilmiş kişisel verilerin işlenmesi, hukuki sebeplerine dayanılarak islenecektir."})]}),i("div",{className:"mt-5",children:[a("h3",{className:"text-1xl font-bold",children:"Verilerin Aktarımı"}),a("p",{className:"mt-5",children:"Uygulamamız aracılığıyla toplanan kişisel verileri, deprem mağdurlarına yardım ve destek sağlamak amacıyla ilgili devlet kurumları ve STK'larla paylaşabiliriz. Kişisel verilerin tüm alıcıları, bilgileri bu gizlilik bildirimine ve KVKK dahil olmak üzere ilgili veri koruma yasalarına uygun olarak kullanmakla yükümlüdür. Deprem mağdurlarına yardım ve destek sağlama amacına ulaşmak için yalnızca gerekli olan minimum miktarda kişisel bilgiyi paylaşacağız. Bazı durumlarda, gizliliği korumak için tarafımızca topladığımız kişisel veriler anonimleştirilebilir veya toplanabilir."})]}),i("div",{className:"mt-5",children:[a("h3",{className:"text-1xl font-bold",children:"Veri Güvenliği"}),a("p",{className:"mt-5",children:"Kişisel verileri yetkisiz erişime, kullanıma veya ifşaya karşı korumak için gerekli idari ve teknik önlemleri almaktayız. Bu önlemler, şifreleme ve güvenli depolama gibi uygun teknik ve organizasyonel önlemlerin uygulanmasını içerir. Ancak, internet üzerinden hiçbir veri aktarımının tamamen güvenli olduğu garanti edilemez. Kişisel verilerin güvenliğini garanti edememekle beraber, korumak için elimizden geleni yapacağız."})]}),i("div",{className:"mt-5",children:[a("h3",{className:"text-1xl font-bold",children:"İlgili Kişi Hakları"}),i("p",{className:"mt-5",children:["KVKK’nin ilgili kişinin haklarını düzenleyen 11. maddesi kapsamındaki taleplerinizi, “Veri Sorumlusuna Başvuru Usul ve Esasları Hakkında Tebliğ’e göre Kisisel"," ",a("a",{href:"mailto:yardimyeri.info@gmail.com",target:"_blank",rel:"noreferrer",className:"underline text-[#0d6efd]",children:"yardimyeri.info@gmail.com"})," ","adresine yazılı olarak iletebilirsiniz."]})]})]})]});export{m as default};
